select canal.idCanal, canal.nome, canal.numero, categoria.nome, programa.titulo, programa.descricao, programa.data, programa.classificacao from canal inner join canal_programa inner join programa inner join categoria inner join canal_categoria on canal_idCanal = idCanal and programa_idPrograma = idPrograma and idCanal_Categoria = idCanal and idCanal_Categoria = idCategoria  where idCanal = 1 